<?php

namespace Cesurapp\MediaBundle\Tests;

use Cesurapp\MediaBundle\Entity\Media;
use Cesurapp\MediaBundle\Manager\MediaManager;
use Cesurapp\StorageBundle\Storage\Storage;
use Doctrine\ORM\Tools\SchemaTool;
use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;
use Symfony\Component\HttpFoundation\File\UploadedFile;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpKernel\KernelInterface;

class ManagerTest extends KernelTestCase
{
    protected function setUp(): void
    {
        static::bootKernel();
        $this->initDatabase(self::$kernel);
    }

    protected function tearDown(): void
    {
        parent::tearDown();
        restore_exception_handler();
    }

    public function testUploadFile(): void
    {
        $request = new Request();
        $request->files->add([
            new UploadedFile(__DIR__.'/resources/image.png', 'image.png'),
            new UploadedFile(__DIR__.'/resources/image.png', 'image.jpg'),
            'subKey' => [new UploadedFile(__DIR__.'/resources/image.png', 'image.png')],
        ]);

        $manager = self::getContainer()->get(MediaManager::class);
        $storage = self::getContainer()->get(Storage::class);
        $em = self::getContainer()->get('doctrine')->getManager();

        // Upload All
        $medias = $manager->uploadFile($request);

        $this->assertInstanceOf(Media::class, $medias[0]);
        $this->assertInstanceOf(Media::class, $medias[1]);
        $this->assertInstanceOf(Media::class, $medias['subKey'][0]);
        $this->assertTrue($storage->device($medias[0]->getStorage())->exists($medias[0]->getPath()));

        array_walk_recursive($medias, static function ($media) use ($em) {
            $em->remove($media);
        });
        $em->flush();

        $this->assertFalse($storage->device($medias[0]->getStorage())->exists($medias[0]->getPath()));
        $this->assertFalse($storage->device($medias[0]->getStorage())->exists($medias[1]->getPath()));
    }

    public function testUploadBase64(): void
    {
        $request = new Request();
        $request->request->add([
            'imageBase64' => '',
        ]);

        $manager = self::getContainer()->get(MediaManager::class);
        $storage = self::getContainer()->get(Storage::class);
        $em = self::getContainer()->get('doctrine')->getManager();

        // Upload All
        $medias = $manager->uploadBase64($request, ['imageBase64'], ['imageBase64' => ['image/png']]);

        $this->assertInstanceOf(Media::class, $medias['imageBase64'][0]);
        $this->assertTrue(
            $storage->device($medias['imageBase64'][0]->getStorage())->exists($medias['imageBase64'][0]->getPath())
        );

        array_walk_recursive($medias, static function ($media) use ($em) {
            $em->remove($media);
        });
        $em->flush();

        $this->assertFalse(
            $storage->device($medias['imageBase64'][0]->getStorage())->exists($medias['imageBase64'][0]->getPath())
        );
    }

    public function testUploadLink(): void
    {
        $request = new Request();
        $request->request->add([
            'filesLink' => 'https://www.google.com.tr/images/branding/googlelogo/1x/googlelogo_light_color_272x92dp.png',
        ]);

        $manager = self::getContainer()->get(MediaManager::class);
        $storage = self::getContainer()->get(Storage::class);
        $em = self::getContainer()->get('doctrine')->getManager();

        // Upload All
        $medias = $manager->uploadLink($request, ['filesLink'], ['filesLink' => ['image/png']]);

        $this->assertInstanceOf(Media::class, $medias['filesLink'][0]);
        $this->assertTrue($storage->device($medias['filesLink'][0]->getStorage())->exists($medias['filesLink'][0]->getPath()));

        array_walk_recursive($medias, static function ($media) use ($em) {
            $em->remove($media);
        });
        $em->flush();

        $this->assertFalse($storage->device($medias['filesLink'][0]->getStorage())->exists($medias['filesLink'][0]->getPath()));
    }

    private function initDatabase(KernelInterface $kernel): void
    {
        if ('test' !== $kernel->getEnvironment()) {
            throw new \LogicException('Execution only in Test environment possible!');
        }

        $entityManager = $kernel->getContainer()->get('doctrine')->getManager();
        $metaData = $entityManager->getMetadataFactory()->getAllMetadata();
        $schemaTool = new SchemaTool($entityManager);
        $schemaTool->updateSchema($metaData);
    }
}
